import { useState, useEffect } from "react"

export default function Clicker(){
    const [count, setCount] = useState(0)

    const buttonClick = () =>{
        setCount(count + 1)
    }

    useEffect(() =>
    {
        console.log('Count changed')
    }, [ count ])

    return <div>
        <div>Clicks: {count}</div>
        <button onClick={buttonClick}>Clicker</button>
    </div>
}
